mapper.select:
select 

from orders
where orders_id = #{orders_id,jdbcType=VARCHAR}
  and items_id = #{items_id,jdbcType=VARCHAR}

mapper.delete:
delete from orders
where orders_id = #{orders_id,jdbcType=VARCHAR}
  and items_id = #{items_id,jdbcType=VARCHAR}

mapper.insert:
insert into orders (orders_id, items_id, buyer_id, 
  seller_id, items_num, price
  )
values (#{orders_id,jdbcType=VARCHAR}, #{items_id,jdbcType=VARCHAR}, #{buyer_id,jdbcType=VARCHAR}, 
  #{seller_id,jdbcType=VARCHAR}, #{items_num,jdbcType=INTEGER}, #{price,jdbcType=DOUBLE}
  )

mapper.update:
update orders
set buyer_id = #{buyer_id,jdbcType=VARCHAR},
  seller_id = #{seller_id,jdbcType=VARCHAR},
  items_num = #{items_num,jdbcType=INTEGER},
  price = #{price,jdbcType=DOUBLE}
where orders_id = #{orders_id,jdbcType=VARCHAR}
  and items_id = #{items_id,jdbcType=VARCHAR}
